import{_ as e,c as i,o as t,a as r}from"./app.4b123bb0.js";const g=JSON.parse('{"title":"Security Acknowledgments","description":"We thank the security researchers who help keep GetLogix Inc. Canada and our clients safe.","frontmatter":{"layout":"page","title":"Security Acknowledgments","description":"We thank the security researchers who help keep GetLogix Inc. Canada and our clients safe."},"headers":[{"level":2,"title":"Responsible Disclosure","slug":"responsible-disclosure","link":"#responsible-disclosure","children":[]},{"level":2,"title":"Our Commitment","slug":"our-commitment","link":"#our-commitment","children":[]},{"level":2,"title":"Hall of Fame","slug":"hall-of-fame","link":"#hall-of-fame","children":[]},{"level":2,"title":"Scope","slug":"scope","link":"#scope","children":[]},{"level":2,"title":"Guidelines","slug":"guidelines","link":"#guidelines","children":[]},{"level":2,"title":"Contact Information","slug":"contact-information","link":"#contact-information","children":[]}],"relativePath":"security-acknowledgments.md"}'),o={name:"security-acknowledgments.md"},a=r('<h1 id="security-acknowledgments" tabindex="-1">Security Acknowledgments <a class="header-anchor" href="#security-acknowledgments" aria-hidden="true">#</a></h1><p>At GetLogix Inc. Canada, we take security seriously and appreciate the efforts of security researchers who help us maintain the safety and integrity of our systems and services.</p><h2 id="responsible-disclosure" tabindex="-1">Responsible Disclosure <a class="header-anchor" href="#responsible-disclosure" aria-hidden="true">#</a></h2><p>We encourage responsible disclosure of security vulnerabilities. If you discover a security issue, please contact us at:</p><ul><li><strong>Email</strong>: <a href="mailto:security@getlogix.com" target="_blank" rel="noreferrer">security@getlogix.com</a></li><li><strong>General Contact</strong>: <a href="mailto:info@getlogix.com" target="_blank" rel="noreferrer">info@getlogix.com</a></li></ul><h2 id="our-commitment" tabindex="-1">Our Commitment <a class="header-anchor" href="#our-commitment" aria-hidden="true">#</a></h2><p>When you report a security vulnerability to us, we commit to:</p><ul><li>Acknowledging receipt of your report within 48 hours</li><li>Providing regular updates on our investigation</li><li>Working with you to understand and resolve the issue</li><li>Giving you proper credit (if desired) once the issue is resolved</li></ul><h2 id="hall-of-fame" tabindex="-1">Hall of Fame <a class="header-anchor" href="#hall-of-fame" aria-hidden="true">#</a></h2><p>We will recognize security researchers who help us improve our security posture. Currently, we are establishing our security acknowledgment program.</p><p><em>This page will be updated as we receive and resolve security reports.</em></p><h2 id="scope" tabindex="-1">Scope <a class="header-anchor" href="#scope" aria-hidden="true">#</a></h2><p>Our security program covers:</p><ul><li><a href="http://GetLogix.com" target="_blank" rel="noreferrer">GetLogix.com</a> website and related domains</li><li>Client applications developed by GetLogix (with client consent)</li><li>Our development infrastructure (where applicable)</li></ul><h2 id="guidelines" tabindex="-1">Guidelines <a class="header-anchor" href="#guidelines" aria-hidden="true">#</a></h2><p>When testing for vulnerabilities:</p><ul><li>Do not access, modify, or delete data that doesn&#39;t belong to you</li><li>Do not perform actions that could harm our services or users</li><li>Do not use automated scanners without prior permission</li><li>Respect user privacy and data protection laws</li></ul><h2 id="contact-information" tabindex="-1">Contact Information <a class="header-anchor" href="#contact-information" aria-hidden="true">#</a></h2><p>For security-related inquiries:</p><ul><li><strong>Primary</strong>: <a href="mailto:security@getlogix.com" target="_blank" rel="noreferrer">security@getlogix.com</a></li><li><strong>General</strong>: <a href="mailto:info@getlogix.com" target="_blank" rel="noreferrer">info@getlogix.com</a></li><li><strong>Phone</strong>: Contact us via email first for security matters</li></ul><hr><p><em>Last updated: July 31, 2025</em></p>',22),l=[a];function n(s,c,u,d,h,m){return t(),i("div",null,l)}const f=e(o,[["render",n]]);export{g as __pageData,f as default};
